!-- Step1: Setup Tables
use PlantInfo; 
DROP TABLE SNotes; 
DROP TABLE FNotes; 
 
DROP TABLE Users; 
DROP TABLE Species; 
DROP TABLE Family; 
 
CREATE TABLE Users( 
	usrid CHAR(36) UNIQUE DEFAULT NEWID(), 
	usrname VARCHAR(50) NOT NULL UNIQUE, 
	passwd CHAR(32) NOT NULL, 
    accessibility CHAR(1) CHECK(accessibility IN ('R', 'A', 'C')) DEFAULT 'C',  
	signUpTime DATETIME DEFAULT GETDATE(), 
	lastLogTime DATETIME DEFAULT NULL, 
	PRIMARY KEY(usrid) 
); 
 
CREATE TABLE Species( 
	spid CHAR(36) UNIQUE DEFAULT NEWID(), 
	sname VARCHAR(50) NOT NULL UNIQUE, 
	nickname VARCHAR(100), 
	sLatin VARCHAR(100) NOT NULL, 
	family VARCHAR(20) NOT NULL, 
	env VARCHAR(100) NOT NULL,  
	region VARCHAR(150) NOT NULL, 
	link VARCHAR(80) CHECK(link LIKE 'http://%' OR link LIKE 'https://%'), 
	specimen_img IMAGE, 
    colorful_img IMAGE, 
	sdesp VARCHAR(1200) NOT NULL, 
	lastEditTime DATETIME DEFAULT GETDATE(), 
	PRIMARY KEY(spid) 
); 
 
CREATE TABLE Family( 
	fid CHAR(36) UNIQUE DEFAULT NEWID(), 
	fname VARCHAR(20) NOT NULL UNIQUE, 
	fLatin VARCHAR(100) NOT NULL, 
	fdesp VARCHAR(1000) NOT NULL, 
	link VARCHAR(80) CHECK(link LIKE 'http://%' OR link LIKE 'https://%'), 
	lastEditTime DATETIME DEFAULT GETDATE(), 
	PRIMARY KEY(fid) 
); 
 
 
--!------ 
CREATE TABLE SNotes( 
	usrid CHAR(36) FOREIGN KEY(usrid) REFERENCES Users(usrid), 
	spid CHAR(36) FOREIGN KEY(spid) REFERENCES Species(spid), 
	isPublic BIT NOT NULL, 
	content VARCHAR(200) NOT NULL, 
	lastEditTime DATETIME DEFAULT GETDATE(), 
	PRIMARY KEY(usrid, spid, isPublic) 
); 
 
CREATE TABLE FNotes( 
	usrid CHAR(36) FOREIGN KEY(usrid) REFERENCES Users(usrid), 
	fid CHAR(36) FOREIGN KEY(fid) REFERENCES Family(fid), 
	isPublic BIT NOT NULL, 
	content VARCHAR(200) NOT NULL, 
	lastEditTime DATETIME DEFAULT GETDATE(), 
	PRIMARY KEY(usrid, fid, isPublic) 
);

