INSERT INTO Species(sname, nickname, sLatin, family, env, region, link, sdesp, specimen_img, colorful_img) 
VALUES('紫珠', NULL, 'Callicarpa bodinieri Levl.', '马鞭草科', '林中林缘灌丛中', '中国南部各省、越南', 'https://baike.baidu.com/item/紫珠', '紫珠（学名：CallicarpabodinieriLevl.）是马鞭草科、紫珠属落叶灌木，高约2米；叶片卵状长椭圆形至椭圆形，顶端长渐尖至短尖，基部楔形，边缘有细锯齿，背面灰棕色，两面密生暗红色或红色细粒状腺点；聚伞花序；苞片细小，线形；花萼外被星状毛和暗红色腺点，萼齿钝三角形；花冠紫色，花药椭圆形，药隔有暗红色腺点，药室纵裂；子房有毛。果实球形，6-7月开花，8-11月结果。分布于中国河南、江苏、安徽、浙江、江西、湖南、湖北、广东、广西、四川、贵州、云南。越南也有分布。生于海拔200-2300米的林中、林缘及灌丛中。该种根或全株入药，能通经和血；治月经不调、虚劳、白带、产后血气痛、感冒风寒；调麻油外用，治缠蛇丹毒。', (SELECT * FROM OPENROWSET(BULK 'C:\MyData\C#_Database\Homeworks\Final Project\RawData\pics\3_specimen.jpg', SINGLE_BLOB) AS img1), (SELECT * FROM OPENROWSET(BULK 'C:\MyData\C#_Database\Homeworks\Final Project\RawData\pics\3_item.jpg', SINGLE_BLOB) AS img2)); 
INSERT INTO Species(sname, nickname, sLatin, family, env, region, link, sdesp, specimen_img, colorful_img) 
VALUES('天目地黄', NULL, 'Rehmannia chingii Li', '玄参科', '生于拔海190-500米之山坡、路旁草丛中', '浙江、安徽', 'https://baike.baidu.com/item/天目地黄', '天目地黄（学名：RehmanniachingiiLi）是玄参科地黄属，多年生半常绿草本。根茎肉质肥厚，橘红色。茎直立，株高30-60厘米，全株被灰白长柔毛或腺毛。基生叶莲座状，叶椭圆形，基部渐狭成长柄；茎生叶向上渐小，上面绿色，皱缩；下面紫红色；叶缘锯齿。总状花序顶生或单生叶腋；花大，花冠筒微弯，外面紫红色，内面黄色油紫斑。蒴果卵形，种子多。花期4-5月。生长于拔海190-500米的山坡、路旁草丛中。分布于浙江、安徽。喜阴湿凉爽环境；稍耐寒；忌酷热。播种或分株繁殖。花大而艳，是良好的观花地被植物，也宜作花境植物材料。主治温热病高热烦躁，吐血衄血，口干，咽喉肿痛，中耳炎，烫伤。', (SELECT * FROM OPENROWSET(BULK 'C:\MyData\C#_Database\Homeworks\Final Project\RawData\pics\4_specimen.jpg', SINGLE_BLOB) AS img1), (SELECT * FROM OPENROWSET(BULK 'C:\MyData\C#_Database\Homeworks\Final Project\RawData\pics\4_item.jpg', SINGLE_BLOB) AS img2)); 
