INSERT INTO Species(sname, nickname, sLatin, family, env, region, link, sdesp, specimen_img, colorful_img) 
VALUES('麻栎', '栎、橡碗树', 'Quercus acutissima Carruth.', '壳斗科', '山中林间路旁', '中国、朝鲜、日本、越南、印度', 'https://baike.baidu.com/item/麻栎', '麻栎（学名：QuercusacutissimaCarruth.）是壳斗科、栎属植物落叶乔木，高可达30米，胸径达1米，树皮深灰褐色，冬芽圆锥形，被柔毛。叶片形态多样，通常为长椭圆状披针形，叶缘有刺芒状锯齿，叶片两面同色，叶柄幼时被柔毛，后渐脱落。雄花序常数个集生于当年生枝下部叶腋，有花，花柱壳斗杯形，小苞片钻形或扁条形，向外反曲，被灰白色绒毛。坚果卵形或椭圆形，顶端圆形，果脐突起。3-4月开花，翌年9-10月结果。分布于中国辽宁、河北、山西、山东、江苏、安徽、浙江、江西、福建、河南、湖北、湖南、广东、海南、广西、四川、贵州、云南等省区。朝鲜、日本、越南、印度也有分布。生于海拔60-2200米的山地阳坡，成小片纯林或混交林。该种木材为环孔材，边材淡红褐色，心材红褐色，气干密度0.8克/立方厘米，材质坚硬，纹理直或斜，耐腐朽，气干易翘裂；供枕木、坑木、桥梁、地板等用材；叶含蛋白质13.58%，可饲柞蚕；种子含淀粉56.4%，可作饲料和工业用淀粉；壳斗、树皮可提取栲胶。', (SELECT * FROM OPENROWSET(BULK 'C:\MyData\C#_Database\Homeworks\Final Project\RawData\pics\69_specimen.jpg', SINGLE_BLOB) AS img1), (SELECT * FROM OPENROWSET(BULK 'C:\MyData\C#_Database\Homeworks\Final Project\RawData\pics\69_item.jpg', SINGLE_BLOB) AS img2)); 
INSERT INTO Species(sname, nickname, sLatin, family, env, region, link, sdesp, specimen_img, colorful_img) 
VALUES('凤丫蕨', NULL, 'Coniogramme japonica (Thunb.) Diels', '裸子蕨科', '生长在海拔100-1300米的湿润林下和山谷阴湿处。', '中国南部、朝鲜、日本', 'https://baike.baidu.com/item/凤丫蕨', '凤丫蕨（学名：Coniogrammejaponica(Thunb.)Diels）是裸子蕨科、凤丫蕨属蕨类植物，植株高可达120厘米。叶柄基部以上光滑；叶片和叶柄等长或稍长，长圆三角形，二回羽状；羽片通常，基部一对最大，卵圆三角形，羽状（偶有二叉）；侧生小羽片披针形，顶生小羽片远较侧生的为大，阔披针形，顶羽片较其下的为大，有长柄；羽片和小羽片边缘有向前伸的疏矮齿。叶脉网状，叶干后纸质，上面暗绿色，下面淡绿色，两面无毛。分布于中国江苏南部、浙江、福建、台湾、江西、安徽、湖北、湖南、广西、四川、贵州、广东。朝鲜南及日本也有分布。生长在海拔100-1300米的湿润林下和山谷阴湿处。凤丫蕨的根茎或全草可作药用，药性味辛、微苦，性凉。归肝经。有祛风除湿，散血止痛，清热解毒之功效。主治风湿关节痛，瘀血腹痛，闭经，跌打损伤，目赤肿痛，乳痈及各种肿毒初起。', (SELECT * FROM OPENROWSET(BULK 'C:\MyData\C#_Database\Homeworks\Final Project\RawData\pics\70_specimen.jpg', SINGLE_BLOB) AS img1), (SELECT * FROM OPENROWSET(BULK 'C:\MyData\C#_Database\Homeworks\Final Project\RawData\pics\70_item.jpg', SINGLE_BLOB) AS img2)); 
